function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body"),t=document.querySelector(".info");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"),t.classList.remove("active")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"),t.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"),t.classList.remove("active"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);var swiper=new Swiper(".mySwiper",{navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});swiper=new Swiper(".mySwipers",{slidesPerView:3,spaceBetween:30,pagination:{el:".swiper-paginations",clickable:!0}});